    private void boutonConsulterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_boutonConsulterActionPerformed
        Vendeur v=app.trouveVendeur(codeVendeur.getText());
        if(v != null)
        {
            try
            {
                new GuiVendeur(v,app).show();
            }
            catch( ExceptionGuiVendeur e)
            {
                JOptionPane.showMessageDialog(null, "Impossible d'afficher la fenetre vendeur: " + e, "alert", JOptionPane.ERROR_MESSAGE); 
            }
        }
    }//GEN-LAST:event_boutonConsulterActionPerformed

