    /**
     * @param args the command line arguments
     */
    public static void main(String args[]) {
            // Trouve les 3 noms de fichier.
            String fichierAutomobile="";
            String fichierVendeurs="";
            String fichierTransaction="";
            
            if(args.length>=3)
            {
                fichierAutomobile=args[0];
                fichierVendeurs=args[1];
                fichierTransaction=args[2];
            }
            else
            {
                JOptionPane.showMessageDialog(null, "Vous devez specifier trois nom de fichier sur la ligne de commande\n ex.: java application automobile.txt vendeurs.txt transaction.txt", "alert", JOptionPane.ERROR_MESSAGE); 
                return; // Quitte le programme.
            }
        app = new Application(fichierVendeurs,fichierAutomobile,fichierTransaction);
        new GuiPrincipal().show();
    }
    
    
